import React, { useState } from "react";

export default function GivePriscription() {
  const [medicines, setMedicines] = useState([]);
  const [dosages, setDosages] = useState([]);

  const handleAddMedicine = () => {
    const medicineName = document.getElementById("medicineName").value;
    const dosage = document.getElementById("dosage").value;
    const newMedicine = {
      name: medicineName,
    };
    const newDosage = {
      dosage: dosage,
    };
    setMedicines([...medicines, newMedicine]);
    setDosages([...dosages, newDosage]);
  };

  const handleDeleteMedicine = (index) => {
    setMedicines(medicines.filter((_, i) => i !== index));
    setDosages(dosages.filter((_, i) => i !== index));
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6">
          <h1>Treatment Details</h1>

        </div>
        <div className="col-md-6">
          <div className="pr-4">
            <h1>Prescription</h1>
            <label htmlFor="medicineName"></label>
            <input type="text" id="medicineName"  placeholder=" Enter Medicine"/>

            <label htmlFor="dosage" ></label>
            <input type="text" id="dosage" placeholder="Enter dosage" />

            <button className="btn btn-warning" onClick={handleAddMedicine}>Add Medicine</button>

            <div className="py-4">
              <table className="table border shadow">
                <thead className="table-header">
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">Medicine</th>
                    <th scope="col">Dosage</th>
                    <th scope="col">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {medicines.map((medicine, index) => (
                    <tr key={index}>
                      <th scope="row">{index + 1}</th>
                      <td>{medicine.name}</td>
                      <td>{dosages[index]?.dosage}</td>
                      <td>
                        <button className="btn btn-danger btn-lg btn-block" onClick={() => handleDeleteMedicine(index)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
