import React, { useEffect, useState } from 'react'
import axios from "axios";
import {useLocation, useNavigate} from 'react-router-dom';
import { Form, Button } from "react-bootstrap";

export default function ReplyQuery() {
    const location = useLocation();
    const [users, setUsers] = useState([]);
    const [userInput, setUserInput] = useState({});

    useEffect(() => {
        loadUsers();
    }, []);

    const loadUsers = async () => {
        try {
            const id=location.state.id.id.id;
            const url = `http://localhost:8081/api/admin/quries`;
            const result = await axios.get(url, { headers: {"Authorization" : `Bearer ${location.state.id.id.token}`} })
            setUsers(result.data);
            // Initialize the user input for each row
            const initialUserInput = {};
            result.data.forEach(user => {
                initialUserInput[user.id] = '';
            });
            setUserInput(initialUserInput);
        } catch (error) {
            console.error(error);
        }
    }

    function reply(id) {
        const text = userInput[id];
        // Do something with the text
    }

    function handleInputChange(event) {
        const { name, value } = event.target;
        setUserInput(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    return (
        <div className='container'>
            <div className='py-4'>
                <table className='table border shadow'>
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Subject</th>
                            <th scope="col">Query</th>
                            <th scope="col">Reply</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user,index)=>(
                            <tr key={user.id}>
                                <th scope="row">{index+1}</th>
                                <td>{user.subject}</td>
                                <td>{user.queryText}</td>
                                <td>
                                    <Form.Control type="text" name={user.id} value={userInput[user.id]} onChange={handleInputChange} />
                                    <Button className="btn btn-danger btn-lg btn-block" onClick={() => reply(user.id)}>Submit</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
