import React, { useEffect, useState } from 'react'
import axios from "axios";
import { Form, Button } from 'react-bootstrap';
export default function PatientBookAppointment() {

    const [users,setUsers]=useState([])

    useEffect(()=>{
        loadUsers();
    },[]);

    const loadUsers = async ()=>{
        const result = await axios.get("http://localhost:8080/api/admin/doctors")
        setUsers(result.data)
    }
    const [formData, setFormData] = useState({ registrationDate: '' });

    const handleChange = (event) => {
      const { name, value } = event.target;
      setFormData({ ...formData, [name]: value });
    };
  return (
    <div className='container'>
        <div className='py-4'>
        <table className="table border shadow">
    <thead>
        <tr>
            <th scope="col">#</th>
            <th scope="col">Name</th>
            <th scope="col">Specialization</th>
            <th scope="col">Timing</th>
            <th scope="col">Date for of Consultation</th>
            <th scope="col">Action</th>
        </tr>
     </thead>
  <tbody>

    {
        users.map((user,index)=>(

        <tr>
        <th scope="row" key={index}>{index+1}</th>
        <td>{user.name}</td>
        <td>{user.specialization}</td>
        <td>{user.timing}</td>
        <td>
        <Form.Label>Registration Date:</Form.Label>
        <Form.Control type="datetime-local" name="registrationDate" value={formData.registrationDate} onChange={handleChange} />
     </td>
        <td>
            <button className='btn btn-primary mx-2'>Book Appointment</button>

        </td>
        </tr>
        ))
    }
       </tbody>
</table>
        </div>
    </div>
  )
}
