import React, { useState } from "react";
import "./GivePrescription.css";

export default function GivePrescription() {
  const [medicines, setMedicines] = useState([]);
  const [dosages, setDosages] = useState([]);

  const handleAddMedicine = () => {
    const medicineName = document.getElementById("medicineName").value;
    const dosage = document.getElementById("dosage").value;
    const newMedicine = {
      name: medicineName,
    };
    const newDosage = {
      dosage: dosage,
    };
    setMedicines([...medicines, newMedicine]);
    setDosages([...dosages, newDosage]);
  };

  const handleDeleteMedicine = (index) => {
    setMedicines(medicines.filter((_, i) => i !== index));
    setDosages(dosages.filter((_, i) => i !== index));
  };

  return (
    <div className="page">
      <div className="section">
        <h1>Treatment Details</h1>
      </div>
      <div className="section">
        <h1>Add Prescription</h1>
        <label htmlFor="medicineName">Medicine Name:</label>
        <input type="text" id="medicineName" />

        <label htmlFor="dosage">Dosage:</label>
        <input type="text" id="dosage" placeholder="Enter dosage here" />

        <button onClick={handleAddMedicine}>Add Medicine</button>

        <div className="py-4">
          <table className="table border shadow">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Medicine</th>
                <th scope="col">Dosage</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {medicines.map((medicine, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{medicine.name}</td>
                  <td>{dosages[index]?.dosage}</td>
                  <td>
                    <button onClick={() => handleDeleteMedicine(index)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
